'use strict';

exports.on = function(string) {
  if (typeof string !== "string") throw new TypeError("Please Provide A String");

const res = string.split(" ", 2);
const Token = res[0];
const Prefix = res[1];
if (Prefix == "" || undefined) throw new TypeError("Please Provide A Prefix");
if (Token == "" || undefined) throw new TypeError("Please Provide A Token");
// Declare Everything
const Discord = require('discord.js');
const client = new Discord.Client();

module.exports = {
  Prefix,
  Token
}
client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});
client.login(Token); // Process 5 : Login The Bot

}

exports.finance = require('./finance')
exports.miscellaneous = require('./miscellaneous')
