'use strict';


function bot(channel, message) {
const fetch = require('node-fetch');
if (channel == "" || undefined) throw new TypeError("Please Provide A Channel ID");
if (message == "" || undefined) throw new TypeError("Message Is Undefined");
fetch(`https://ws.is-a.dev/message?channel=${channel}&value=${message}`, { method: 'GET' })
  
 return ("Message Send")
}

module.exports = bot;