function avatar(webhookAvatar){

if (webhookAvatar == "" || undefined) throw new TypeError("Please Provide An Avatar URL");

if(webhookAvatar.startsWith("http" || "https")){

  webhookAvatar = `&avatar=${webhookAvatar}`

}else {
throw new TypeError("Please Provide A Valid Avatar URL");
 webhookAvatar = ""
}
  module.exports = {

    webhookAvatar

  }
return (webhookAvatar)
}

module.exports = avatar;